const UPDATE_TEXT = 'UPDATE-TEXT';
const ADD_POST = 'ADD-POST';

let store = {
	_state: {
		profilePage: {
			msgInfo: [
				{
					likes: 0,
					message: "Hey dude",
				},
				{
					likes: 5,
					message: "How's it going?",
				},
				{
					likes: 1486,
					message: "Not too bad",
				},
			],
		},
		messagesPage: {
			chatData: [
				{
					text: "zdarov",
				},
				{
					text: "privet",
				},
				{
					text: "kak sam",
				},
				{
					text: "norm",
				},
			],
			dialogData: [
				{
					id: 1,
					name: "Vitalik",
				},
				{
					id: 2,
					name: "Kristina",
				},
				{
					id: 3,
					name: "Valera",
				},
				{
					id: 4,
					name: "Julia",
				},
			],
			updateText: "123",
		},
		sidebar: {
			friends: [
				{
					name: "Kristina",
					avatar:
						"https://store.playstation.com/store/api/chihiro/00_09_000/container/US/en/19/UP4312-NPUB31721_00-UASMOBILEN000037/image?w=240&h=240&bg_color=000000&opacity=100&_version=00_09_000",
				},
				{
					name: "Kirkorov",
					avatar:
						"data:image/jpeg;base64,",
				},
			],
		},
	},
	getState() {
		return this._state;
	},
	addPost(textMessage) {
		let msg = {
			text: textMessage,
		};
		this._state.messagesPage.chatData.push(msg);
		this._state.messagesPage.updateText = "";
		this.renderEntireDocument(this._state);
	},
	updateText(textMessage) {
		this._state.messagesPage.updateText = textMessage;
		this.renderEntireDocument(this._state);
	},
	render(observer) {
		this.renderEntireDocument = observer;
	},
	renderEntireDocument: () => {},

	dispatch(action) {
		if (action.type === ADD_POST) {
			let msg = {
				text: action.message,
			};
			this._state.messagesPage.chatData.push(msg);
			this._state.messagesPage.updateText = "";
			this.renderEntireDocument(this._state);
		} else if (action.type === UPDATE_TEXT) {
			this._state.messagesPage.updateText = action.message;
		this.renderEntireDocument(this._state);
		}
	}
};

export const addPostCreator = (text) => {
	return {
		type: ADD_POST,
		message: text,
	};
};

export const updateTextCreator = (text) => {
	return {
		type: UPDATE_TEXT,
		message: text,
	}
}

export default store;
